          orientation: orientation,
          model: {
            uri: 'http://localhost:3000/object/cow.glb',
            scale: 10.0
          },
        }); */
    }

    render(){
        return <div style={{height: "54vh", width: "50%"}} id="map3d"/>
    }
}
